import { Bar } from 'react-chartjs-2';
import {
  Box,
  Button,
  Card,
  CardContent,
  CardHeader,
  Divider,
  Slider,
  Typography,
  Grid
} from '@material-ui/core';
import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';
import ArrowRightIcon from '@material-ui/icons/ArrowRight';
import { useState } from 'react';



const Training = (props) => {
  const [epoch, setEpoch] = useState(10);
  const [batch, setBatch] = useState(10);
  const [learningRate, setLearningRate] = useState(10);


  const handleChange = (event, newValue) => {
    if (typeof newValue === 'number') {
      if (event.target.name == "epoch") setEpoch(newValue);
      if (event.target.name == "batch") setBatch(newValue);
      if (event.target.name == "learningRate") setLearningRate(newValue);
    }
  };

  return (
    <Card {...props}>
      <CardHeader
        title="Training"
      />

      <Divider />
      <CardContent>

    <Grid justifyContent="center" container spacing={2}>
    <Grid item xs={4}>
      <Box sx={{ width: 150 }}>
      <Typography id="non-linear-slider" gutterBottom>
        에포크: {epoch}
      </Typography>
      <Slider
        value={epoch}
        min={5}
        step={1}
        max={30}
        name="epoch"
        onChange={handleChange}
        valueLabelDisplay="auto"
        aria-labelledby="non-linear-slider"
      />
    </Box>
    </Grid>

    <Grid item xs={4}>
    <Box sx={{ width: 250 }}>
      <Typography id="non-linear-slider" gutterBottom>
        배치: {batch}
      </Typography>
      <Slider
        value={batch}
        name="batch"
        min={5}
        step={1}
        max={30}
        onChange={handleChange}
        valueLabelDisplay="auto"
        aria-labelledby="non-linear-slider"
      />
    </Box>
    </Grid>

    <Grid item xs={4}>
    <Box sx={{ width: 250 }}>
      <Typography id="non-linear-slider" gutterBottom>
        학습률: {learningRate}
      </Typography>
      <Slider
        value={learningRate}
        min={5}
        step={1}
        max={30}
        name="learningRate"
        onChange={handleChange}
        valueLabelDisplay="auto"
        aria-labelledby="non-linear-slider"
      />
    </Box>
    </Grid>

    </Grid>
        {/* <Box
          sx={{
            height: 400,
            position: 'relative'
          }}
        >
          <Bar
            data={data}
            options={options}
          />
        </Box> */} 딥러닝 시작
          <Button>
            FIT
          </Button>
          <br/><br/>
            확인 이미지
          <Button>
            upload
          </Button>
      </CardContent>


      <Divider />
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'flex-end',
          p: 2
        }}
      >
        <CardContent>
        결과를 보여주는 창
        </CardContent>
      </Box>
    </Card>
  );
};

export default Training;
