import { Bar } from 'react-chartjs-2';
import {
  Box,
  Button,
  Card,
  CardContent,
  CardHeader,
  Divider,
  Slider,
  Typography,
  Grid
} from '@material-ui/core';
import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';
import ArrowRightIcon from '@material-ui/icons/ArrowRight';
import { useState } from 'react';



const Training = (props) => {
  const [epoch, setEpoch] = useState(10);
  const [batch, setBatch] = useState(10);
  const [learningRate, setLearningRate] = useState(0.01);


  const handleChange = (event, newValue) => {
    if (typeof newValue === 'number') {
      if (event.target.name == "epoch") setEpoch(newValue);
      if (event.target.name == "batch") setBatch(newValue);
      if (event.target.name == "learningRate") setLearningRate(newValue);
    }
  };

  return (
    <Card {...props}>
      <CardHeader
        title="Training"
      />

      <Divider />
      <CardContent>

    <Grid container spacing={2}>
    <Grid item xs={4}>
      <Box sx={{ width: 250 }}>
      <Typography id="non-linear-slider" gutterBottom>
        Epoch: {epoch}
      </Typography>
      <Slider
        value={epoch}
        min={5}
        step={1}
        max={50}
        name="epoch"
        onChange={handleChange}
        valueLabelDisplay="auto"
        aria-labelledby="non-linear-slider"
      />
    </Box>
    </Grid>

    <Grid item xs={4}>
    <Box sx={{ width: 250 }}>
      <Typography id="non-linear-slider" gutterBottom>
        Batch: {batch}
      </Typography>
      <Slider
        value={batch}
        name="batch"
        min={1}
        step={1}
        max={100}
        onChange={handleChange}
        valueLabelDisplay="auto"
        aria-labelledby="non-linear-slider"
      />
    </Box>
    </Grid>

    <Grid item xs={4}>
    <Box sx={{ width: 250 }}>
      <Typography id="non-linear-slider" gutterBottom>
        Learning Rate: {learningRate}
      </Typography>
      <Slider
        value={learningRate}
        min={0.001}
        step={0.001}
        max={1}
        name="learningRate"
        onChange={handleChange}
        valueLabelDisplay="auto"
        aria-labelledby="non-linear-slider"
      />
    </Box>
    </Grid>
    </Grid>
    <br/><br/>
    <Divider />

    <Grid container spacing={2}>
      <Grid item xs={6}>
      <Typography sx={{pt:2}} align="right">
        딥러닝 학습하기
      </Typography>
      </Grid>
      <Grid item xs={6}>
          <Button
          sx={{pt:2, my:2}}
          variant="contained"
          size="large">
            FIT
          </Button>
        </Grid>
    </Grid>

      </CardContent>


      <Divider />
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'flex-end',
          p: 2
        }}
      >
        <CardContent>
        결과를 보여주는 창
        </CardContent>
      </Box>
    </Card>
  );
};

export default Training;
