import { useState } from 'react';
import {
  Box,
  Button,
  Card,
  CardContent,
  CardHeader,
  Divider,
  Grid,
  TextField,
  Input
} from '@material-ui/core';

import axios from 'axios'
import { useSelector, useDispatch } from 'react-redux';
import { Form, Formik } from 'formik';

const AccountProfileDetails = (props) => {
  const loginstate = useSelector(state => state.loggin);
  // state = { selectedFiles: null };
  const [selectedFiles, setSelectedFiles] = useState(null);


  const onClickHandler = e => {
    var formData = new FormData();
    // console.log(selectedFiles.length)
    for (var i=0; i<selectedFiles.length; i++){
      formData.append("image[]", selectedFiles[i]);
    }
    // formData.append("image[]", selectedFiles[0]);
    console.log(formData);
    axios.post('http://localhost:4000/imgupload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
    })

    // axios.post(`http://localhost:4000/test`, formData, {headers: headers});
  };


  const fileChangedHandler = e => {
    const files = e.target.files;
    setSelectedFiles( selectedFiles => selectedFiles = files);

  };

  const nameviewer = e => {
    var arr = new Array();
    for (var i=0; i<e.length; i++){
      arr.append(e[i].name)
    }
    return arr
  };

  return (
    <form
      action = "http://localhost:4000/imgupload" method = "POST" enctype = "multipart/form-data"
      autoComplete="off"
      noValidate
      {...props}
    >
      <Card>
        <CardHeader
          subheader="딥러닝 이미지 추가 / 제거"
          title="Profile"
        />
        <Divider />
        <CardContent>
          <Grid
            container
            spacing={3}
          >
            <Grid
              item
              md={6}
              xs={12}
            >
              이미지 추가          
            </Grid>
            <Grid
              item
              md={6}
              xs={12}
            >

  {/* <input name="FILE[]" style={{ display: 'none' }} accept="image/*" type="file" multiple onChange={fileChangedHandler}/> */}

  {/* <input name="FILE[]" type="file" multiple onChange={fileChangedHandler}/> */}
  {/* <input type="submit" /> */}
  {/* <Input variant="contained" type="submit"/> */}


  {/* <Button variant="contained" component="span" onClick={onClickHandler} >
    Upload
  </Button> */}

  {/* <input
  accept="image/*"
  // className={classes.input}
  style={{ display: 'none' }}
  multiple
  type="file"
  onChange={fileChangedHandler}
  /> */}
  <Button variant="contained" component="label">
    Explorer
    <input
  accept="image/*"
  // className={classes.input}
  style={{ display: 'none' }}
  multiple
  type="file"
  onChange={fileChangedHandler}
  />
  </Button>


  <Button variant="contained" component="span" onClick={onClickHandler} >
    Upload
  </Button>
                  {/* <Button>
                Upload
                </Button> */}
            </Grid>


            <Grid
              item
              md={6}
              xs={12}
            >
              이미지 제거
            </Grid>
            <Grid
              item
              md={6}
              xs={12}
            >
              <Button>
                DELETE
                </Button>
            </Grid>

            <Grid
              item
              md={6}
              xs={12}
            >
              이미지 클라우드 용량
            </Grid>
            <Grid
              item
              md={6}
              xs={12}
            >
              5기가 중 4.12기가
            </Grid>
          </Grid>
        </CardContent>
        <Divider />
        <Box
          sx={{
            display: 'flex',
            justifyContent: 'flex-end',
            p: 2
          }}
        >
          이미지 업로드 시 업로딩 바 표기
        </Box>
      </Card>

      <Card>
        {selectedFiles
        ?nameviewer(selectedFiles).forEach(element => {
          {element}
        })
        :null
        }
      </Card>
    </form>
  );
};

export default AccountProfileDetails;
