import { useState } from 'react';
import {
  Box,
  Button,
  Card,
  CardContent,
  CardHeader,
  Divider,
  Grid,
  TextField,
  Input
} from '@material-ui/core';

import axios from 'axios'
import { useSelector, useDispatch } from 'react-redux';

const AccountProfileDetails = (props) => {
  const loginstate = useSelector(state => state.loggin);
  // state = { selectedFiles: null };
  const [selectedFiles, setSelectedFiles] = useState({"files" : null});


  const onClickHandler = e => {
    const headers = {
      'Content-Type': 'multipart/form-data'
    }

    const formData = new FormData();
    // formData.append(
    //   // "uploadImages",
    //   // selectedFiles.files
    //   // selectedFiles.files.name
    //   'data', "ad"
    // );
    formData.append("uploadImages", selectedFiles.files, selectedFiles.files.name);

    // const config = {
    //   headers: {
    //     "content-type": false
    //   }
    // };

    axios.post(`http://localhost:4000/test`, formData, {headers: headers});
  };


  const fileChangedHandler = e => {
    const files = e.target.files;
    console.log(files);
    setSelectedFiles( selectedFiles => selectedFiles.files = files);
    // const newBlob = new Blob(selectedFiles.files);
    // console.log(newBlob);
    // const chunk = files[0].slice(0, 1024, 'image/png');
    // console.log(chunk);

  };

  return (
    <form
      autoComplete="off"
      noValidate
      {...props}
    >
      <Card>
        <CardHeader
          subheader="딥러닝 이미지 추가 / 제거"
          title="Profile"
        />
        <Divider />
        <CardContent>
          <Grid
            container
            spacing={3}
          >
            <Grid
              item
              md={6}
              xs={12}
            >
              이미지 추가          
            </Grid>
            <Grid
              item
              md={6}
              xs={12}
            >
  {/* <Input accept="image/*" id="contained-button-file" type="file" multiple/> */}

  <input name="FILE" type="file" multiple onChange={fileChangedHandler}/>
  <input type="submit" />


  <Button variant="contained" component="span" onClick={onClickHandler} >
    Upload
  </Button>

                  {/* <Button>
                Upload
                </Button> */}
            </Grid>

            <Grid
              item
              md={6}
              xs={12}
            >
              이미지 제거
            </Grid>
            <Grid
              item
              md={6}
              xs={12}
            >
              <Button>
                DELETE
                </Button>
            </Grid>

            <Grid
              item
              md={6}
              xs={12}
            >
              이미지 클라우드 용량
            </Grid>
            <Grid
              item
              md={6}
              xs={12}
            >
              5기가 중 4.12기가
            </Grid>
          </Grid>
        </CardContent>
        <Divider />
        <Box
          sx={{
            display: 'flex',
            justifyContent: 'flex-end',
            p: 2
          }}
        >
          이미지 업로드 시 업로딩 바 표기
        </Box>
      </Card>
    </form>
  );
};

export default AccountProfileDetails;
