import moment from 'moment';
import {
  Avatar,
  Box,
  Button,
  Card,
  CardActions,
  CardContent,
  Divider,
  Typography
} from '@material-ui/core';


import axios from 'axios'
import { useSelector } from 'react-redux';
import { useState } from 'react';


const AccountProfile = (props) => {
  const loginstate = useSelector(state => state.loggin);
  const [cloudList, setCloudList] = useState(null);


  const user = {
    avatar: '/static/images/avatars/avatar_3.png',
    city: 'Los Angelesasd',
    country: 'USA',
    jobTitle: 'Senior Developer',
    name: loginstate.email
  };


  const cloudviewer = () => {
    var carr = [];
    axios.get('http://localhost:4000/test').then(function (response) {
      console.log(response.data.length);
      console.log(response.data[1].name);
      for (var j=0; j < response.data.length; j++){
        carr.push(<li>{response.data[j].name}</li>);
        // arr.push(response);
      };
    });
    // setCloudList(carr);
    // console.log(arr);
    // console.log(cloudList);
    return carr
  };

  return(
  <Card {...props}>
    <CardContent>
      <Box
        sx={{
          alignItems: 'center',
          display: 'flex',
          flexDirection: 'column'
        }}
      >
        <Avatar
          src={user.avatar}
          sx={{
            height: 100,
            width: 100
          }}
        />
        <Typography
          color="textPrimary"
          gutterBottom
          variant="h3"
        >
          {user.name}
        </Typography>
        <Typography
          color="textSecondary"
          variant="body1"
        >
          {`${user.city} ${user.country}`}
        </Typography>
        <Typography
          color="textSecondary"
          variant="body1"
        >
          {`${moment().format('hh:mm A')} ${user.timezone}`}
        </Typography>
      </Box>
    </CardContent>
    <Divider />
    <CardActions>
      <Button
        color="primary"
        fullWidth
        variant="text"
      >
        Upload picture
      </Button>
    </CardActions>
     <Card>
       <Button onClick={cloudviewer}>클라우드 저장소 조회</Button> <br/>

      {cloudList
      ?<ol>{cloudviewer()}</ol>
      :null
      }
      arr
     </Card>
  </Card>
);
}

export default AccountProfile;
